import React from "react";

// import { Link } from "react-router-dom";

class PostList extends React.Component {
    constructor() {
        super();

        this.state = {
            posts: []
        };
    }
    componentDidMount() {
        let postsURL = "http://localhost/wp-json/wp/v2/posts?_embed";
        fetch(postsURL)
            .then(response => response.json())
            .then(response => {
                this.setState({
                    posts: response
                });
            });
    }
    render() {
        let posts = this.state.posts.map((post, index) => {
            console.log(post._embedded["wp:featuredmedia"]["0"]);
            return (
                <div
                    key={index}
                    className="card card-odd col-md-6 col-sm-12 border-0"
                >
                    <img
                        className="card-img-top"
                        src={`post._embedded["wp:featuredmedia"][0].media_details
                                .sizes["large"].source_url`}
                        alt="Card scap"
                    />
                    <div className="card-body">
                        <h4 className="text-center alice card-title">
                            {post.title.rendered}
                        </h4>
                        <h6 className="card-subtitle mb-2 alice text-center orange">
                            Apr 30, 2019
                        </h6>
                        <p className="card-text">
                            Some quick example text to build on the card title
                            and make up the bulk of the card's content.
                        </p>
                        <a
                            href="#!"
                            className="orange text-uppercase font-weight-bold"
                        >
                            read more
                        </a>
                    </div>
                </div>
            );
        });
        return (
            <div className="container mt-5">
                <div className="row card-deck">{posts}</div>
            </div>
        );
    }
}
export default PostList;
