import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";

class PostList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            posts: []
        };
    }
    componentDidMount() {
        axios.get("http://localhost:3000/wp-json/wp/v2/posts").then(posts => {
            this.setState({
                posts: posts.data
            });
        });
    }
    render() {
        return (
            <div className="container mt-5">
                <div className="row card-deck">
                    <div className="card card-odd col-md-6 col-sm-12 border-0">
                        <img
                            className="card-img-top"
                            src="../imgs/yukalog-pic2.jpg"
                            alt="Card scap"
                        />
                        <div className="card-body">
                            <h4 className="text-center alice card-title">
                                Card title
                            </h4>
                            <h6 className="card-subtitle mb-2 alice text-center orange">
                                Apr 30, 2019
                            </h6>
                            <p className="card-text">
                                Some quick example text to build on the card
                                title and make up the bulk of the card's
                                content.
                            </p>
                            <a
                                href="#!"
                                className="orange text-uppercase font-weight-bold"
                            >
                                read more
                            </a>
                        </div>
                    </div>
                    <div className="card card-even col-md-6 col-sm-12 border-0">
                        <img
                            className="card-img-top"
                            src="../imgs/yukalog-pic2.jpg"
                            alt="Card cap"
                        />
                        <div className="card-body">
                            <h4 className="text-center alice card-title">
                                Card title
                            </h4>
                            <h6 className="card-subtitle mb-2 alice text-center orange">
                                Apr 5, 2019
                            </h6>
                            <p className="card-text">
                                Some quick example text to build on the card
                                title and make up the bulk of the card's
                                content.
                            </p>
                            <a
                                href="#!"
                                className="orange text-uppercase font-weight-bold"
                            >
                                read more
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
export default PostList;
